<?php\n// Example plugin header\n/*\nPlugin Name: My First Plugin\nDescription: A simple plugin to demonstrate plugin development\nVersion: 1.0\n*/\n\n// Example hook to add a custom function to the WordPress admin dashboard\nadd_action('wp_dashboard_setup', 'my_plugin_dashboard_widget');\n\nfunction my_plugin_dashboard_widget() {\n    add_dashboard_widget('my_plugin_widget', 'My Plugin Widget', 'my_plugin_widget_callback');\n}\n\nfunction my_plugin_widget_callback() {\n    echo '<p>Hello, World!</p>';\n}