function smartPagination($currentPage, $totalPages, $adjacentPages = 2) {\n    $startPage = max(1, $currentPage - $adjacentPages);\n    $endPage = min($totalPages, $currentPage + $adjacentPages);\n\n    $pagination = array();\n\n    // Add "..." if there are pages before the start page\n    if ($startPage > 1) {\n        $pagination[] = "...";\n    }\n\n    // Add pages from start page to end page\n    for ($i = $startPage; $i <= $endPage; $i++) {\n        $pagination[] = $i;\n    }\n\n    // Add "..." if there are pages after the end page\n    if ($endPage < $totalPages) {\n        $pagination[] = "...";\n    }\n\n    return $pagination;\n}\n\n// Example usage:\n$currentPage = 5;\n$totalPages = 11;\n$pagination = smartPagination($currentPage, $totalPages);\n\nprint_r($pagination);