<?php\n// command.php\n\n// Define the available commands\n$commands = [\n    'create-controller' => 'createController',\n    'create-module' => 'createModule',\n];\n\n// Get the command-line arguments\n$args = $_SERVER['argv'];\n\n// Check if a command is provided\nif (isset($args[1]) && array_key_exists($args[1], $commands)) {\n    // Execute the corresponding action\n    call_user_func($commands[$args[1]]);\n} else {\n    echo "Usage: php command.php <command>\n";\n    exit(1);\n}\n\n// Define the functions for each command\nfunction createController()\n{\n    // Code to create a controller goes here\n    echo "Controller created successfully!\n";\n}\n\nfunction createModule()\n{\n    // Code to create a module goes here\n    echo "Module created successfully!\n";\n}