$file = fopen('example.csv', 'r');\nstream_set_blocking($file, 0);\n\n$buffer = '';\nwhile (!feof($file)) {\n    $char = fgetc($file);\n    if ($char === "\r" && fgetc($file) === "\n") {\n        // Found 0x0d0a line termination, process the buffer\n        processBuffer($buffer);\n        $buffer = '';\n    } else {\n        $buffer .= $char;\n    }\n}\n\n// Process the last buffer\nprocessBuffer($buffer);\n\nfclose($file);\n\nfunction processBuffer($buffer) {\n    // Process the buffer as a single line\n    // You can use explode or str_getcsv to parse the CSV data\n    $data = str_getcsv($buffer);\n    print_r($data);\n}