function create_node($title, $body, $cck_fields) {\n  // Create a new node object\n  $node = new stdClass();\n  $node->type = 'your_node_type'; // Replace with your node type\n  $node->title = $title;\n  $node->body = $body;\n\n  // Add CCK fields\n  foreach ($cck_fields as $field_name => $field_value) {\n    $node->$field_name = array(\n      array('value' => $field_value),\n    );\n  }\n\n  // Save the node\n  node_save($node);\n}