$mysqli = new mysqli("your_host", "your_username", "your_password", "your_database");\n\nif ($mysqli->connect_errno) {\n    echo "Failed to connect to MySQL: (" . $mysqli->connect_errno . ") " . $mysqli->connect_error;\n}\n\n$stmt = $mysqli->prepare("INSERT IGNORE INTO locations (name, address, city, state, zip) VALUES (?, ?, ?, ?, ?)");\n$stmt->bind_param("sssss", $venueName, $streetAddress, $city, $state, $zip);\n\nif (!$stmt->execute()) {\n    echo "Error: " . $stmt->error;\n}