use PhpJsParser\Parser;\nuse PhpJsParser\Node\FunctionDeclaration;\n\n$parser = new Parser();\n$jsCode = 'function my_function(hey) { do stuff }';\n$ast = $parser->parse($jsCode);\n\nforeach ($ast->getFunctions() as $function) {\n    if ($function->getName() === 'my_function') {\n        $newFunction = new FunctionDeclaration('new_function', ['hi'], 'do different stuff');\n        $ast->replaceNode($function, $newFunction);\n    }\n}\n\n$newJsCode = $ast->toString();\necho $newJsCode;