$normal_array       = array();\n$array_of_arrayrefs = array(&$normal_array);\n\n// Get the last element of the $array_of_arrayrefs array, which is a reference to $normal_array\n$last_ref = &$array_of_arrayrefs[count($array_of_arrayrefs) - 1];\n\n// Now you can modify the $normal_array through the $last_ref reference\n$last_ref["one"] = 1;\n\nprint $normal_array["one"]; // outputs 1