function RandomSite($intNumberofSites = 1) {\n    $arrOutput = NULL;\n    GetDatabaseConnection('dev');\n\n    $strSQL = "\n        SELECT id\n        FROM (\n            SELECT id, RAND() AS rand_value\n            FROM site_info\n            WHERE major <> 0\n        ) AS subquery\n        ORDER BY rand_value\n        LIMIT $intNumberofSites\n    ";\n\n    $result = @mysql_query($strSQL);\n    $i = -1;\n\n    while ($row = mysql_fetch_array($result, MYSQL_NUM)) {\n        $arrOutput[$i++] = $row[0];\n    }\n\n    return $arrOutput;\n}