function convert_word_text_to_html($text) {\n    // Convert to UTF-8 encoding\n    $text = iconv('Windows-1252', 'UTF-8', $text);\n    \n    // Convert special characters to HTML entities\n    $text = htmlspecialchars($text, ENT_QUOTES, 'UTF-8');\n    \n    // Replace curly quotes and ellipses with their HTML entity equivalents\n    $text = str_replace("\xE2\x80\x9C", '&ldquo;', $text); // left curly quote\n    $text = str_replace("\xE2\x80\x9D", '&rdquo;', $text); // right curly quote\n    $text = str_replace("\xE2\x80\xA6", '&hellip;', $text); // ellipsis\n    \n    return $text;\n}\n\n// Example usage:\n$word_text = 'Example text with “curly quotes” and … ellipsis.';\n$html_text = convert_word_text_to_html($word_text);\necho $html_text;