$dateTime = new DateTime('2022-07-25 14:30:00');\n$unixTimestamp = $dateTime->getTimestamp();\n\n// Insert into database using datetime2 data type\n$sql = "INSERT INTO table (datetime_column) VALUES ('2022-07-25 14:30:00')";\n\n// Retrieve from database and convert to PHP DateTime object\n$sql = "SELECT datetime_column FROM table";\n$result = sqlsrv_query($conn, $sql);\n$dateTime = DateTime::createFromFormat('Y-m-d H:i:s', sqlsrv_get_field($result, 0));\n\n// Convert to UNIX timestamp\n$unixTimestamp = $dateTime->getTimestamp();