$arrayObject = new ArrayObject(array('value1', 'value2', 'value3'));\n\nfunction arrayObject_in_array($needle, ArrayObject $haystack) {\n    foreach ($haystack as $value) {\n        if ($value === $needle) {\n            return true;\n        }\n    }\n    return false;\n}\n\nvar_dump(arrayObject_in_array('value2', $arrayObject)); // outputs: bool(true)\nvar_dump(arrayObject_in_array('value4', $arrayObject)); // outputs: bool(false)