$xml_data = '<?xml version="1.0" encoding="UTF-8"?><root>...</root>'; // your XML data\n\n// compress the XML data\n$compressed_data = gzcompress($xml_data, 9); // 9 is the highest compression level\n\n// set the headers to force a download\nheader('Content-Type: application/x-gzip');\nheader('Content-Disposition: attachment; filename="data.xml.gz"');\nheader('Content-Length: ' . strlen($compressed_data));\n\n// output the compressed data\necho $compressed_data;