$ids = [1, 2, 3, 4, 5]; // your array of IDs\n\n// Create a MySQLi instance (or use your existing database connection)\n$mysqli = new mysqli('localhost', 'your_username', 'your_password', 'your_database');\n\n// Update query with parameterized IDs\n$stmt = $mysqli->prepare('UPDATE your_table SET column1 = ? WHERE id = ?');\n\n// Loop through the array and execute the query for each ID\nforeach ($ids as $id) {\n    $stmt->bind_param('ii', $id, 'your_new_value'); // Replace with the actual value you want to update\n    $stmt->execute();\n}