$ids = [1, 2, 3, 4, 5]; // your array of IDs\n\n// Create a PDO instance (or use your existing database connection)\n$dsn = 'mysql:host=localhost;dbname=your_database';\n$username = 'your_username';\n$password = 'your_password';\n$pdo = new PDO($dsn, $username, $password);\n\n// Update query with parameterized IDs\n$stmt = $pdo->prepare('UPDATE your_table SET column1 = :value WHERE id = :id');\n\n// Loop through the array and execute the query for each ID\nforeach ($ids as $id) {\n    $stmt->execute([\n        ':id' => $id,\n        ':value' => 'your_new_value', // Replace with the actual value you want to update\n    ]);\n}