<?php\n$memcached = new Memcached();\n$memcached->addServer('localhost', 11211);\n\n// Cache key\n$cacheKey = 'file_exists_' . $filename;\n\n// Check cache first\nif ($memcached->get($cacheKey) === true) {\n    // File exists, display accordingly\n} else {\n    // Check database and file system\n    $result = $db->query("SELECT * FROM files WHERE filename = '$filename'");\n    if ($result->num_rows > 0 && file_exists($filename)) {\n        // File exists, cache the result\n        $memcached->set($cacheKey, true, 3600); // cache for 1 hour\n        // Display accordingly\n    } else {\n        // File does not exist, cache the result\n        $memcached->set($cacheKey, false, 3600); // cache for 1 hour\n        // Display accordingly\n    }\n}