function findUniqueChars($str) {\n    $charCount = array();\n    for ($i = 0; $i < strlen($str); $i++) {\n        $char = $str[$i];\n        if (!isset($charCount[$char])) {\n            $charCount[$char] = 1;\n        } else {\n            $charCount[$char]++;\n        }\n    }\n    $uniqueChars = array();\n    foreach ($charCount as $char => $count) {\n        if ($count == 1) {\n            $uniqueChars[] = $char;\n        }\n    }\n    return $uniqueChars;\n}