$table_name = 'your_table_name';\n$columns = array('columnA', 'columnB');\n\n$stmt = $wpdb->prepare("SELECT COLUMN_NAME \n                        FROM INFORMATION_SCHEMA.COLUMNS \n                        WHERE TABLE_NAME = %s \n                          AND COLUMN_NAME IN (%s)", \n                       $table_name, \n                       implode(',', array_fill(0, count($columns), '%s')));\n\n$result = $wpdb->get_results($stmt, ARRAY_A);