public function bind($hook, $value)\n{\n    $this->dbBind[$hook] = $value;\n}\n\npublic function run()\n{\n    $sql = $this->dbSql;\n    $stmt = mysql_prepare($this->dbConn, $sql);\n    if (is_array($this->dbBind)) {\n        foreach ($this->dbBind as $hook => $value) {\n            mysql_bind_param($stmt, $hook, $value);\n        }\n    }\n    mysql_execute($stmt) or die(mysql_error());\n    $this->dbBind = array();\n    return mysql_num_rows($stmt);\n}