$measurements = array(1469, 1465, 739, 849);\n$diffs = array(0, 0, 0, 0);\n\n// Update the value in the $diffs array\n$diffs[2] = 200;\n\n// Loop through the $diffs array to find the updated index\nforeach ($diffs as $index => $value) {\n    if ($value != 0) {\n        // Subtract the value from the corresponding index in the $measurements array\n        $measurements[$index] -= $value;\n    }\n}\n\nprint_r($measurements);