// en.php (English translations)\n$lang = array(\n    'hello' => 'Hello',\n    'goodbye' => 'Goodbye',\n    'operation_not_allowed' => 'This operation is not allowed!',\n);\n\n// es.php (Spanish translations)\n$lang = array(\n    'hello' => 'Hola',\n    'goodbye' => 'Adiós',\n    'operation_not_allowed' => 'Esta operación no está permitida!',\n);\n\n// lang.php (language management)\nclass Lang {\n    private $lang;\n\n    public function __construct($lang) {\n        $this->lang = $lang;\n    }\n\n    public function getTranslation($key) {\n        $langFile = $this->lang . '.php';\n        if (file_exists($langFile)) {\n            require $langFile;\n            return isset($lang[$key]) ? $lang[$key] : 'Translation not found';\n        } else {\n            return 'Language file not found';\n        }\n    }\n}\n\n// Usage\n$lang = new Lang($_GET['lang']);\necho $lang->getTranslation('hello'); // Outputs "Hello" for lang=en or "Hola" for lang=es