function calculateDistance($zip1, $zip2) {\n  // Connect to your MySQL database\n  $db = new mysqli('your_host', 'your_username', 'your_password', 'your_database');\n\n  // Retrieve the latitude and longitude for both zip codes\n  $query = "SELECT LATITUDE, LONGITUDE FROM your_table WHERE ZIP = '$zip1'";\n  $result = $db->query($query);\n  $row = $result->fetch_assoc();\n  $lat1 = $row['LATITUDE'];\n  $lon1 = $row['LONGITUDE'];\n\n  $query = "SELECT LATITUDE, LONGITUDE FROM your_table WHERE ZIP = '$zip2'";\n  $result = $db->query($query);\n  $row = $result->fetch_assoc();\n  $lat2 = $row['LATITUDE'];\n  $lon2 = $row['LONGITUDE'];\n\n  // Calculate the distance using the Haversine formula\n  $theta = $lon1 - $lon2;\n  $dist = sin(deg2rad($lat1)) * sin(deg2rad($lat2)) +  cos(deg2rad($lat1)) * cos(deg2rad($lat2)) * cos(deg2rad($theta));\n  $dist = acos($dist);\n  $dist = rad2deg($dist);\n  $miles = $dist * 60 * 1.1515;\n\n  return $miles;\n}\n\n// Helper functions\nfunction deg2rad($deg) {\n  return $deg * pi() / 180;\n}\n\nfunction rad2deg($rad) {\n  return $rad * 180 / pi();\n}