function processSmsResponse($message, $code) {\n    // Convert message to lowercase\n    $message = strtolower($message);\n    $code = strtolower($code);\n\n    // Remove code and space from message\n    $digits = str_replace($code . ' ', '', $message);\n\n    // Check if digits match the expected format (5 digits between 1 and 5)\n    if (!preg_match('/^[1-5]{5}$/', $digits)) {\n        // Handle errors\n        if (strlen($digits) < 5) {\n            return 'Error: Too few digits.';\n        } elseif (strlen($digits) > 5) {\n            return 'Error: Too many digits.';\n        } elseif (!ctype_digit($digits)) {\n            return 'Error: Non-digit characters.';\n        } else {\n            return 'Error: Invalid digit values.';\n        }\n    }\n\n    // Split digits into individual variables\n    $digit1 = (int) substr($digits, 0, 1);\n    $digit2 = (int) substr($digits, 1, 1);\n    $digit3 = (int) substr($digits, 2, 1);\n    $digit4 = (int) substr($digits, 3, 1);\n    $digit5 = (int) substr($digits, 4, 1);\n\n    // Store digits in DB or return success message\n    return 'Success: Digits stored.';\n}\n\n// Example usage:\n$message = 'Freetrip 12345';\n$code = 'Freetrip';\n\n$result = processSmsResponse($message, $code);\necho $result;