<?php\nheader('Content-Type: text/html; charset=UTF-8');\n\n// Using PDO for a new connection\n$pdo = new PDO('mysql:host=localhost;dbname=your_db', 'your_user', 'your_pass',\n    array(PDO::MYSQL_ATTR_INIT_COMMAND => "SET NAMES utf8"));\n\n// Example query\n$stmt = $pdo->prepare("SELECT your_column FROM your_table WHERE id = :id");\n$stmt->execute(array('id' => 1));\n$result = $stmt->fetch();\n\necho $result['your_column']; // This should display special characters correctly.\n?>