<?php\n// Load database configuration from a secure file or environment variables\n$dbHost = 'localhost';\n$dbUsername = 'monty';\n$dbPassword = 'indian';\n$dbName = 'sachin';\n\n// Connect to database using PDO\n$dsn = "mysql:host=$dbHost;dbname=$dbName";\n$conn = new PDO($dsn, $dbUsername, $dbPassword);\n\n// Prepare and execute the query\n$stmt = $conn->prepare("SELECT p.page_url AS url, COUNT(*) AS occurrences \n                        FROM page p, word w, occurrence o\n                        WHERE p.page_id = o.page_id AND\n                        w.word_id = o.word_id AND\n                        w.word_word = :keyword\n                        GROUP BY p.page_id\n                        ORDER BY occurrences DESC");\n$stmt->bindParam(':keyword', $_POST['keyword']);\n$stmt->execute();\n\n// Create a new DOMDocument object\n$output = new DOMDocument();\n$output->formatOutput = true;\n\n// Iterate over the query results and build the XML output\nforeach ($stmt->fetchAll() as $row) {\n    $output->appendChild($output->createElement('person'));\n    $output->appendChild($output->createElement('keyword', $_POST['keyword']));\n    $output->appendChild($output->createElement('name', $row['url']));\n    $output->appendChild($output->createElement('occur', $row['occurrences']));\n}\n\n// Save the XML output to a file\n$output->saveXML('output.xml');