// caching layer (e.g., Redis)\n$redis = new Redis();\n$redis->connect('localhost', 6379);\n\n// function to get approximate number of search results\nfunction getApproximateResults($regionId) {\n  // check if cached result exists\n  $cachedResult = $redis->get("approx_results:$regionId");\n  if ($cachedResult) {\n    return $cachedResult;\n  }\n\n  // perform query to get approximate number of results\n  $db = new mysqli('localhost', 'username', 'password', 'database');\n  $query = "SELECT COUNT(*) as approximate_count FROM your_table WHERE region_id = $regionId LIMIT 1";\n  $result = $db->query($query);\n  $approximateCount = $result->fetch_assoc()['approximate_count'];\n\n  // cache result for 1 hour\n  $redis->set("approx_results:$regionId", $approximateCount, 3600);\n\n  return $approximateCount;\n}\n\n// usage\n$regionId = $_GET['regionId'];\n$approximateResults = getApproximateResults($regionId);\necho "Approximate number of search results: $approximateResults";