// check_cookies.php\n\n// Set a test cookie\nsetcookie('cookie_test', 'ok', time() + 3600);\n\n// Redirect to the same page with a query string\nheader('Location: ' . $_SERVER['PHP_SELF'] . '?cc=1');\nexit;\n\n// Check the cookie value after redirection\nif (isset($_COOKIE['cookie_test']) && $_COOKIE['cookie_test'] == 'ok') {\n    echo 'Cookies are enabled';\n} else {\n    echo 'Cookies are disabled';\n}