class AclassName {\n    public function doSomething() {\n        echo "Doing something!";\n    }\n}\n\nclass MyClass {\n    private $foo;\n\n    public function __construct(AclassName $foo) {\n        $this->foo = $foo;\n    }\n\n    public function doSomethingElse() {\n        $this->foo->doSomething();\n    }\n}\n\n// Create an instance of AclassName\n$aClassInstance = new AclassName();\n\n// Create an instance of MyClass, passing the AclassName instance\n$myClassInstance = new MyClass($aClassInstance);\n\n// Call the doSomethingElse method, which uses the AclassName instance\n$myClassInstance->doSomethingElse(); // Output: Doing something!