function getDistance($origin, $destination, $mode = 'driving') {\n    $api_key = 'YOUR_API_KEY';\n    $url = 'https://maps.googleapis.com/maps/api/distancematrix/json?origins=' . urlencode($origin) . '&destinations=' . urlencode($destination) . '&mode=' . $mode . '&key=' . $api_key;\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    $response = curl_exec($ch);\n    curl_close($ch);\n    $data = json_decode($response, true);\n    if ($data['status'] == 'OK') {\n        return $data['rows'][0]['elements'][0]['distance']['text'];\n    } else {\n        return 'Error';\n    }\n}\n\necho getDistance('New York, USA', 'Los Angeles, USA');