// check_javascript.php\n\n// Create a hidden form field\necho '<input type="hidden" id="js_enabled" name="js_enabled" value="">';\n\n// Add JavaScript to fill the field\necho '<script>document.getElementById("js_enabled").value = "yes";</script>';\n\n// Check the field value after submission\nif ($_POST['js_enabled'] == 'yes') {\n    echo 'JavaScript is enabled';\n} else {\n    echo 'JavaScript is disabled';\n}