$alert = null;\n\nfunction validate($from, $body) {\n    if (empty($from) || empty($body)) {\n        return false;\n    }\n    return true;\n}\n\nfunction sendEmail($from, $body) {\n    // Use a more secure way to send emails, such as using a library or framework\n    mail("example@example.com", "Example Email", $body, "From: $from");\n}\n\nfunction fuss() {\n    global $alert;\n    $alert = "Dolor Sit";\n}\n\nfunction alert() {\n    global $alert;\n    if (!is_null($alert)) {\n        echo $alert;\n    }\n}\n\nif (isset($_POST["Return"])) {\n    $from = filter_var($_POST["From"], FILTER_SANITIZE_EMAIL);\n    $body = filter_var($_POST["Body"], FILTER_SANITIZE_STRING);\n    if (validate($from, $body)) {\n        sendEmail($from, $body);\n        $alert = "Lorem Ipsum";\n    } else {\n        fuss();\n    }\n}