$url = 'http://example.com';\n$ch = curl_init($url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n$response = curl_exec($ch);\ncurl_close($ch);\n\n// Check for RSS links in HTML header\n$headers = explode("\n", $response);\nforeach ($headers as $header) {\n    if (preg_match('/<link.*type="application\/rss+xml".*>/', $header, $matches)) {\n        $rss_url = preg_replace('/.*href="([^"]+)".*/', '$1', $matches[0]);\n        // $rss_url is the RSS feed associated with the URL\n    }\n}\n\n// Check for RSS links in HTML body\n$body = preg_replace('/.*<body>/', '', $response);\npreg_match_all('/<a.*href="([^"]+)".*>(.*?)<\/a>/', $body, $matches);\nforeach ($matches[1] as $link) {\n    if (preg_match('/application\/rss+xml/', $link)) {\n        $rss_url = $link;\n        // $rss_url is the RSS feed associated with the URL\n    }\n}