<?php\n// Assuming you have the user input stored in $username and $password\n\n// Set the encryption key (store this securely, e.g., as an environment variable)\n$encryption_key = 'your_secret_key_here';\n\n// Encrypt the data using OpenSSL\n$encrypted_data = openssl_encrypt(json_encode(['username' => $username, 'password' => $password]), 'AES-256-CBC', $encryption_key, 0, substr($encryption_key, 0, 16));\n\n// Set the encrypted cookie\nsetcookie('auth_data', $encrypted_data, time() + 3600, '/'); // expires in 1 hour