class Model_User extends Model_Person\n{\n    protected static $_gateway;\n\n    public static function init()\n    {\n        if (self::$_gateway == null) {\n            self::$_gateway = new Model_Table_User();\n        }\n    }\n\n    public static function get($searchString, $searchType = 'id')\n    {\n        self::init();\n\n        // Use the Model_Person gateway to retrieve the person data\n        $person = parent::get($searchString, $searchType);\n\n        // Use the Model_User gateway to retrieve the user data\n        $userRow = self::$_gateway->find($person->getId())->current();\n\n        return self::factory($person, $userRow);\n    }\n\n    public function login()\n    {\n        // Use the Model_User gateway to perform the login logic\n        // ...\n    }\n\n    public function setPassword()\n    {\n        // Use the Model_User gateway to update the user's password\n        // ...\n    }\n\n    protected static function factory(Model_Person $person, Zend_Db_Table_Row $userRow)\n    {\n        $class = get_called_class();\n        return new $class($person, $userRow);\n    }\n\n    protected function __construct(Model_Person $person, Zend_Db_Table_Row $userRow)\n    {\n        parent::__construct($person);\n        $this->_user = $userRow;\n    }\n}