// Tag.php (your Tag model)\npublic function getTagsWithOccurrences()\n{\n    $tagObjects = $this->findDependentRowset('TagObject');\n    $tags = array();\n    foreach ($tagObjects as $tagObject) {\n        $tag = $tagObject->findParentRow('Tag');\n        $tags[] = array(\n            'tag' => $tag,\n            'occurrences' => $tagObject->findDependentRowset('Object')->count()\n        );\n    }\n    return $tags;\n}