<?php\n// Get the encryption key (same as in Page 1)\n$encryption_key = 'your_secret_key_here';\n\n// Get the encrypted cookie\n$encrypted_data = $_COOKIE['auth_data'];\n\n// Decrypt the data using OpenSSL\n$decrypted_data = openssl_decrypt($encrypted_data, 'AES-256-CBC', $encryption_key, 0, substr($encryption_key, 0, 16));\n\n// Unserialize the decrypted data\n$data = json_decode($decrypted_data, true);\n\n// Use the decrypted data\n$username = $data['username'];\n$password = $data['password'];