<?php\n// Connect to database\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Display comments\n$bug_id = $_GET['bug_id'];\n$sql = "SELECT * FROM comments WHERE bug_id = '$bug_id'";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        echo "<p>" . $row["comment"] . "</p>";\n        echo "<p>Posted by " . $row["user_id"] . " on " . $row["datetime"] . "</p>";\n    }\n} else {\n    echo "No comments yet.";\n}\n\n// Add new comment\nif (isset($_POST['submit'])) {\n    $comment = $_POST['comment'];\n    $user_id = $_SESSION['user_id']; // Assuming you have a user session\n    $bug_id = $_GET['bug_id'];\n    $sql = "INSERT INTO comments (bug_id, user_id, comment, datetime) VALUES ('$bug_id', '$user_id', '$comment', NOW())";\n    $conn->query($sql);\n}\n\n$conn->close();\n?>