<?php\n$xmlDoc = new DOMDocument();\nif (!$xmlDoc->load("testrtap11.xml")) {\n    echo "Error parsing XML";\n    exit;\n}\n\n$mysqli = new mysqli("your_host", "your_user", "your_password", "zeeshan_database1");\n\nif ($mysqli->connect_error) {\n    echo "Error connecting to database: " . $mysqli->connect_error;\n    exit;\n}\n\n$x = $xmlDoc->getElementsByTagName('RECORD');\n$z = $xmlDoc->getElementsByTagName('TITLE');\n$w = $xmlDoc->getElementsByTagName('PRIMARY_AUTHOR');\n$y = $xmlDoc->getElementsByTagName('JOURNAL_CONFERENCE');\n\nfor ($i = 0; $i <= $x->length - 1; $i++) {\n    $zz = $mysqli->real_escape_string($z->item($i)->nodeValue);\n    $ww = $mysqli->real_escape_string($w->item($i)->nodeValue);\n    $yy = $mysqli->real_escape_string($y->item($i)->nodeValue);\n\n    $sql = "INSERT INTO Persons (FirstName, LastName, Age) VALUES ('$zz', '$ww', '$yy')";\n\n    if (!$mysqli->query($sql)) {\n        echo "Error: " . $mysqli->error;\n        exit;\n    }\n    echo "1 record added";\n}\n\n$mysqli->close();\n?>