$file = 'path/to/your/file.txt';\n\n// Acquire an exclusive lock on the file\n$fp = fopen($file, 'w');\nif (flock($fp, LOCK_EX)) {\n    // You now have an exclusive lock on the file\n    // Make your changes to the file here\n    fwrite($fp, 'New data');\n    // Release the lock\n    flock($fp, LOCK_UN);\n} else {\n    // Unable to acquire lock, handle error\n    echo 'Error: Unable to acquire lock on file.';\n}\nfclose($fp);