function is_valid_utf8($text) {\n    return mb_check_encoding($text, 'UTF-8');\n}\n\nfunction get_correct_string($text) {\n    if (is_valid_utf8($text)) {\n        return $text;\n    } else {\n        $utf8_text = utf8_decode($text);\n        if (is_valid_utf8($utf8_text)) {\n            return $utf8_text;\n        } else {\n            // Handle the case where both strings are invalid\n            return null;\n        }\n    }\n}