function createRSS() {\n    $articles = Article::getAll("order by created desc limit 10"); // Limit to 10 items\n    $num_articles = count($articles);\n\n    // Set the RSS file path and name\n    $rss_file = 'rss.xml';\n\n    // Create the RSS file\n    $fp = fopen($rss_file, 'w');\n    if (!$fp) {\n        // Can't write to a file\n        return;\n    }\n\n    // Write the RSS header\n    fwrite($fp, '<?xml version="1.0" encoding="UTF-8" ?>' . "\n");\n    fwrite($fp, '<rss version="2.0" xmlns:atom="http://www.w3.org/2005/Atom">' . "\n");\n    fwrite($fp, '<channel>' . "\n");\n\n    // Write the RSS channel information\n    fwrite($fp, '<title>Kiubbo.com</title>' . "\n");\n    fwrite($fp, '<link>http://www.kiubbo.com/</link>' . "\n");\n    fwrite($fp, '<description>Todas las Noticias Fotos y Videos.</description>' . "\n");\n    fwrite($fp, '<language>es-mx</language>' . "\n");\n    fwrite($fp, '<docs>http://www.kiubbo.com/rss.xml</docs>' . "\n");\n\n    // Write the RSS items\n    foreach ($articles as $article) {\n        $url = $article->getUrl();\n        $title = $article->getTitle();\n\n        // Remove HTML tags and special characters from the title\n        $title = strip_tags($title);\n        $title = htmlspecialchars($title);\n\n        fwrite($fp, '<item>' . "\n");\n        fwrite($fp, '  <title>' . $title . '</title>' . "\n");\n        fwrite($fp, '  <link>' . $url . '</link>' . "\n");\n        fwrite($fp, '  <guid>http://www.kiubbo.com</guid>' . "\n");\n        fwrite($fp, '</item>' . "\n");\n    }\n\n    // Close the RSS channel and file\n    fwrite($fp, '</channel>' . "\n");\n    fwrite($fp, '</rss>' . "\n");\n    fclose($fp);\n}