function custom_rewrite_rules() {\n    add_rewrite_rule('^discusses/?$', 'index.php?pagename=articles', 'top');\n    add_rewrite_rule('^discussed/([^/]+)/?$', 'index.php?post_type=post&name=$matches[1]&section=articles', 'top');\n    add_rewrite_rule('^does/?$', 'index.php?pagename=portfolio', 'top');\n    add_rewrite_rule('^designed/([^/]+)/?$', 'index.php?post_type=post&name=$matches[1]&section=portfolio', 'top');\n}\nadd_action('init', 'custom_rewrite_rules');