public function showRowAction()\n{\n    $id = 10;\n    $article = Mage::getModel('mycompany_mymodule/article')->getArticleById($id);\n    $this->loadLayout();\n    $this->getLayout()->getBlock('content')->append(\n        $this->getLayout()->createBlock('core/template')\n            ->setTemplate('mycompany/mymodule/show_row.phtml')\n            ->setArticle($article)\n    );\n    $this->renderLayout();\n}