<?php\nuse PHPMailer\PHPMailer\PHPMailer;\n\n// Assume $name, $email, and $token are set from user input\n\n$db = new PDO('...'); // Connect to your database\n\n// Store the signature in the database\n$stmt = $db->prepare('INSERT INTO petition_signatures (name, email, token) VALUES (:name, :email, :token)');\n$stmt->execute(['name' => $name, 'email' => $email, 'token' => $token]);\n\n// Send the email\n$mail = new PHPMailer();\n$mail->isSMTP();\n$mail->Host = 'your-smtp-server.com';\n$mail->SMTPSecure = 'tls';\n$mail->Port = 587;\n$mail->From = $email;\n$mail->FromName = $name;\n$mail->addAddress('recipient@example.com');\n$mail->isHTML(true);\n$mail->Subject = 'Petition Signature';\n$mail->Body = '<p>Standard text message with <strong>' . $name . '</strong> as the signature.</p>';\n$mail->AltBody = 'Plain text version of the email';\n\nif (!$mail->send()) {\n    // Handle email sending error\n}