// Form handler\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    // Validate form data\n    $errors = [];\n    if (empty($_POST['name'])) {\n        $errors[] = 'Name is required';\n    }\n\n    // If there are errors, store the input data in a session\n    if (!empty($errors)) {\n        $_SESSION['form_data'] = $_POST;\n        // Redirect back to the form page\n        header('Location: form.php');\n        exit;\n    }\n}\n\n// Form page\nif (isset($_SESSION['form_data'])) {\n    $form_data = $_SESSION['form_data'];\n    unset($_SESSION['form_data']); // Remove the data from the session\n}\n\n// Use the stored data to populate the input fields\n?>\n<input type="text" name="name" value="<?= $form_data['name'] ?? '' ?>">