function get_header_image($post_id) {\n    $header_image = get_post_meta($post_id, 'header_image', true);\n    if (!$header_image) {\n        $parent = get_page($post_id);\n        while (!$header_image && $parent->post_parent) {\n            $header_image = get_post_meta($parent->post_parent, 'header_image', true);\n            $parent = get_page($parent->post_parent);\n        }\n    }\n    return $header_image;\n}