$name = new Zend_Form_Element_Text('name');\n$name->setLabel('Name');\n\n// Create a custom decorator to add the link\n$decorator = new Zend_Form_Decorator_HtmlTag(array('tag' => 'dd'));\n$decorator->setOptions(array('escape' => false));\n$name->addDecorator($decorator);\n\n// Add the link to the decorator\n$name->setDescription('<a href="#">My Link</a>');\n$name->addDecorator(new Zend_Form_Decorator_Description(array('tag' => 'span', 'class' => 'link')));\n\n$this->addElements($name);\n$this->addDisplayGroup(array('name'), 'people');