function validateEmail($email) {\n    $hostname = substr(strrchr($email, "@"), 1);\n    $fp = fsockopen($hostname, 25, $errno, $errstr, 30);\n    if (!$fp) {\n        return false;\n    }\n    $out = "HELO " . $_SERVER['SERVER_NAME'] . "\r\n";\n    $out .= "MAIL FROM: <" . $_SERVER['SERVER_ADMIN'] . ">\r\n";\n    $out .= "RCPT TO: <$email>\r\n";\n    $out .= "QUIT\r\n";\n    fwrite(STDOUT, $out);\n    $response = '';\n    while (!feof($STDIN)) {\n        $response .= fread($STDIN, 4096);\n    }\n    fclose($STDIN);\n    if (preg_match('/250/', $response)) {\n        return true;\n    } else {\n        return false;\n    }\n}