$data = array(\n    array('Area1', null, null),\n    array(null, 'Section1', null),\n    array(null, null, 'Location1'),\n    array('Area2', null, null),\n    array(null, 'Section2', null),\n    array(null, null, 'Location2')\n);\n$root = array();\n$area = null;\n$section = null;\nforeach ($data as $row) {\n    if ($row[0]) {\n        $root[$row[0]] = array();\n        $area =& $root[$row[0]];\n    } elseif ($row[1]) {\n        $area[$row[1]] = array();\n        $section =& $area[$row[1]];\n    } elseif ($row[2]) {\n        $section[] = $row[2];\n    }\n}\nprint_r($root);