$db = new mysqli('your_host', 'your_username', 'your_password', 'bookfeather');\n\n$tables = $db->query('SHOW TABLES');\nwhile ($table = $tables->fetch_assoc()) {\n  $tableName = $table['Tables_in_bookfeather'];\n  $query = "INSERT INTO aggregated_votes (site, total_votes_up)\n            SELECT site, SUM(votes_up) AS total_votes_up\n            FROM $tableName\n            GROUP BY site";\n  $db->query($query);\n}